package com.sample;

public class even_add {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1000;
		for(int i=1;i<=a;i++) {
			
		//	System.out.println(i);
			if(i%2==0) {
				System.out.println(" values "+i);
			}
			else
			{
				if(i%2!=0) {
					System.out.println(i);
				}
			}
		}

	}

}
